<?phpnamespace App;use Illuminate\Database\Eloquent\Model;class Config extends Model{    protected $fillable = [        'name', 'content', 'user_id', 'created_at', 'updated_at'    ];    public function users()    {        return $this->belongsTo('App\User', 'user_id');    }    public function getAllConfig()    {        return $this->pluck('content', 'name');    }    public function updateConfig($key, $val)    {        if ($setting = self::getAllSettings()->where('name', $key)->first()) {            return $setting->update([                'name' => $key,                'content' => $val]) ? $val : false;        }    }    public static function getAllSettings()    {        return self::all();    }    public function getConfigByName($name)    {        return $this->whereName($name)->first()->content;    }    public function getConfigByListName($array){        return $this->whereIn('name',$array)->get();    }}