<?phpnamespace App\Http\Controllers;use App\Config;use App\Repositories\Backend\Config\ConfigRepositoryInterface;use Illuminate\Http\Request;class ConfigGeneralController extends Controller{    protected $configRepository;    public function __construct(ConfigRepositoryInterface $configRepository)    {        $this->configRepository = $configRepository;    }    public function getConfig()    {        $cauhinhs = $this->configRepository->getAllConfig();        return view('backend.admin.config.index', compact('cauhinhs'));    }    public function saveConfig(Request $request)    {        $cauhinhs = $this->configRepository->updateAllConfig($request);        return redirect()->route('config.index');    }}