<?phpnamespace App\Http\Controllers;use App\Mail\SendingMailToCustomer;use App\Mail\SendingMailToMeContact;use App\Mail\SendingMailToMeSidebar;use Illuminate\Http\Request;use Illuminate\Support\Facades\Mail;use App\Mail\SendingMailToMeFooter;use Illuminate\Support\Facades\Validator;class MailController extends Controller{    public function sendMailContact(Request $request){        $validator = Validator::make($request->all(), [            'name' => 'required',            'email'=>'required|email',            'phone'=>'required'        ],[            'name.required' => 'Mời Quý Khách Nhập Tên Liên Hệ',            'email.required' => 'Mời Quý Khách Nhập Email',            'email.email' => 'Mời Quý Khách Nhập Nhập Đúng Định Dạng Email',            'phone.required' => 'Xin Vui Lòng Nhập Số Điện Thoại',        ]);        if ($validator->fails()) {            return response()->json([                'success' => false,                'validator'=>$validator->errors()            ]);        }        Mail::send(new SendingMailToMeContact());        Mail::send(new SendingMailToCustomer());        return response()->json([            'success' => true        ]);    }}